            $theme_bof_file = $folder_ent_path . "/bof.php";
            if ( !empty( $Config ) )
            unset( $Config );
            require( $theme_bof_file );
            if ( !empty( $Config ) ){
              $_themes[ $folder_ent ] = array(
                "ID" => $folder_ent,
                "name" => $Config["name"],
                "image" => is_file( $folder_ent_path . "/preview.png" ) ? themes_address . "{$folder_ent}/preview.png" : false,
                "detail" => !empty( $Config["detail"] ) ? $Config["detail"] : false
              );
            }
          }

        }
      }
    }

    return $_themes;

  }

  public function get_all(){

    $list = bof()->plug->list( "theme" );
    $active_theme = bof()->object->db_setting->get( "theme" );

    foreach( $list as $themeName => &$themeArgs ){

      $themeArgs["active"] = false;

      if ( $active_theme == $themeName ){
        $themeArgs["active"] = true;
        $themeArgs["price_label"] = "Active";
        $themeArgs["btn"]["childs"][] = array(
          "title" => "Setting",
          "class" => "setting",
          "link" => "theme_setting"
        );
      } elseif ( $themeArgs["installed"] ) {
        $themeArgs["btn"]["title"] = "Activate";
        $themeArgs["btn"]["childs"][] = array(
          "title" => "Activate",
          "class" => "activate",
          "attr" => "data-theme-id='{$themeArgs["code"]}'"
        );
      }

    }

    return $list;

  }

  public function get(){

    $active_theme_name = bof()->object->db_setting->get( "theme" );
    $active_theme_dir_path = realpath( themes_root . "/" . $active_theme_name );
    $active_theme_bof_path = realpath( $active_theme_dir_path . "/" . "bof.php" );
    if ( !$active_theme_bof_path ) fall("Invalid theme");
    require( $active_theme_bof_path );

    $active_theme_data = bof()->plug->read( "theme", $active_theme_name );

    $Config["parts"] = !empty( $Config["parts"] ) ? $Config["parts"] : [];
    $Config["assets"]["js"] = !empty( $Config["assets"]["js"] ) ? $Config["assets"]["js"] : [];
    $Config["assets"]["css"] = !empty( $Config["assets"]["css"] ) ? $Config["assets"]["css"] : [];

    if ( $Config["parts"] ){
      foreach( $Config["parts"] as &$_part ){
        $_part[0] = str_replace( "__SELF__", themes_address . $active_theme_name . "/", $_part[0] );
        if ( !empty( $_part[1]["base"] ) ) $_part[1]["base"] = str_replace( "__SELF__", themes_address . $active_theme_name . "/", $_part[1]["base"] );
      }
    }

    $uploadCSS = false;
    foreach( [ "css", "js" ] as $_asset_k ){
      if ( $Config["assets"][ $_asset_k ] ){
        foreach( $Config["assets"][ $_asset_k ] as $ii => &$_asset ){
          if ( !empty( $_asset["mobileOnly"] ) && !bof()->request->is_mobile() ){
            $_asset = false;
            continue;
          }
          $_asset["path"] = str_replace( "__SELF__", themes_address . $active_theme_name . "/", $_asset["path"] );
          if ( !empty( $_asset["base"] ) ) $_asset["base"] = str_replace( "__SELF__", themes_address . $active_theme_name . "/", $_asset["base"] );
          $_asset["version"] = $active_theme_data["version"];
          if ( !empty( $_asset["uploadOnly"] ) ){
            $uploadCSS = $_asset;
            $_asset = false;
          }
        }
      }
      if ( !empty( $Config["admin_assets"]["page_builder"][ $_asset_k ] ) ){
        foreach( $Config["admin_assets"]["page_builder"][ $_asset_k ] as &$_asset ){
          $_asset["path"] = str_replace( "__SELF__", themes_address . $active_theme_name . "/", $_asset["path"] );
          if ( !empty( $_asset["base"] ) ) $_asset["base"] = str_replace( "__SELF__", themes_address . $active_theme_name . "/", $_asset["base"] );
        }
      }
    }

    if ( !empty( $Config["page_themes"] ) ){
      foreach( $Config["page_themes"] as &$page_theme ){
        if ( !empty( $page_theme["args"]["base"] ) ? $page_theme["args"]["base"] == "__SELF__" : false ){
          $page_theme["args"]["base"] = str_replace( "__SELF__", themes_address . $active_theme_name . "/", $page_theme["args"]["base"] );
        }
      }
    }

    $menus_ps = bof()->object->db_setting->get( "menus_ps", array(
      "desk" => 1,
      "mob" => 2,
      "footer" => null
    ) );

    return array(
      "address" => themes_address . $active_theme_name . "/",
      "name" => $Config["name"],
      "version" => $Config["version"],
      "parts" => !empty( $Config["parts"] ) ? $Config["parts"] : [],
      "page_themes" => !empty( $Config["page_themes"] ) ? $Config["page_themes"] : [],
      "assets" => array(
        "css" => !empty( $Config["assets"]["css"] ) ? $Config["assets"]["css"] : [],
        "js" => !empty( $Config["assets"]["js"] ) ? $Config["assets"]["js"] : [],
        "upload" => $uploadCSS
      ),
      "admin_assets" => !empty( $Config["admin_assets"] ) ? $Config["admin_assets"] : [],
      "menus" => array(
        "sidebar" => bof()->object->menu->get( bof()->request->is_mobile() ? $menus_ps["mob"] : $menus_ps["desk"] ),
        "footer" => !empty($menus_ps["footer"]) ? bof()->object->menu->get( $menus_ps["footer"] ) : null
      ),
      "supported_objects" => !empty( $Config["supported_objects"] ) ? $Config["supported_objects"] : [],
      "setting" => array(
        "upload_button" => bof()->object->db_setting->get("upload_button"),
        "upgrade_button" => bof()->object->db_setting->get("upgrade_button"),
        "offline_download_button" => bof()->object->db_setting->get("offline_download_button"),
      ),
      /*"css_variables" => array(
        "color" => "0,154,210"
      )*/
    );

  }

  public function run_functions(){

    $active_theme_name = bof()->object->db_setting->get( "theme" );
    $active_theme_dir_path = realpath( themes_root . "/" . $active_theme_name );
    $active_theme_bof_path = realpath( $active_theme_dir_path . "/" . "bof_functions_".(bof()->getName()=="bof_client"?"client":"admin").".php" );
    if ( !$active_theme_bof_path ) return false;

    $bof = bof();
    require_once( $active_theme_bof_path );

  }

  public function set( $name ){

     bof()->object->db_setting->set( "theme", $name );

  }

  public function admin_setting( $end, $groups, $inputs=[] ){

    $fonts = json_decode( $this->fonts, 1 );

    foreach( $fonts as $font_name => $font_sizes ){
      $options[] = [ $font_name, $font_name ];
    }

    $groups["font"]["inputs"]["font_name"]["input"]["options"] = $options;
    $groups["font"]["inputs"]["font_name"]["validator"][1]["values"] = array_keys( $fonts );

    if ( $end == "ui" ){
      $menus_ps = bof()->object->db_setting->get( "menus_ps", array(
        "desk" => 1,
        "mob" => 2,
        "footer" => null
      ) );
      if ( !empty( $menus_ps ) ){
        foreach( $menus_ps as $menu_p => $menu_id ){
          if ( !empty( $groups["menus"]["inputs"][ "menu_p_{$menu_p}" ] ) ){
            $groups["menus"]["inputs"][ "menu_p_{$menu_p}" ]["input"]["value"] = $menu_id;
          }
        }
      }
    }
    if ( $end == "be_after" ){

      if ( !empty( $inputs["set"]["theme_color"] ) ){

        $color_hex = $inputs["set"]["theme_color"];
        list( $r, $g, $b ) = sscanf( $color_hex, "%02x%02x%02x" );
        $color_rgb = "{$r}, {$g}, {$b}";

        $inputs["set"]["theme_color_rgb"] = $color_rgb;

      }

      if ( empty( $inputs["data"]["menu_p_mob"] ) )
      $inputs["report"]["fail"]["menu_p_mob"] = "Select one";

      if ( empty( $inputs["data"]["menu_p_desk"] ) )
      $inputs["report"]["fail"]["menu_p_desk"] = "Select one";

      if ( empty( $inputs["rpoert"]["fail"] ) ){
        bof()->object->db_setting->set( "menus_ps", json_encode( array(
          "desk" => $inputs["data"]["menu_p_desk"],
          "mob" => $inputs["data"]["menu_p_mob"],
          "footer" => $inputs["data"]["menu_p_footer"],
        ) ), "json" );
      }

      return $inputs;

    }

    return $groups;

  }

}

